    public function getSourceContext()
    {
        return new Source("{% extends 'admin.html.twig' %}

{% block title %}Hello AdminOrderContholler!{% endblock %}

{% block body %}
    <table class=\"table\">
        <thead>
            <tr>
                <th>Title</th>
                <th>Manufacturer</th>
                <th>Customer name</th>
                <th>Customer phone</th>
                <th>Customer address</th>
                <th>Product quantity</th>
                <th>Order quantity</th>
                <th>Status</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            {% for order in orders %}
            <tr>
            <td>{{ order.title }}</td>
                <td>{{ order.manufacturer | upper }}</td>
                <td>{{ order.name }}</td>
                <td>{{ order.phone_number }}</td>
                <td>{{ order.address }}</td>
                <td>{{ order.quantity }}</td>
                <td>{{ order.order_quantity }}</td>
                <td>{{ order.status }}</td>
                {% if order.status == 'pending' %}
                <td><a href=\"{{ path('approve', {id: order.id}) }}\" class=\"btn btn-primary\">Approve</a></td>
                {% else %}
                <td><a href=\"{{ path('cancel', {id: order.id}) }}\" class=\"btn btn-danger\">Cancel</a></td>
                {% endif %}
            </tr>
            {% endfor %}
        </tbody>
    </table>
{% endblock %}
", "admin_order/index.html.twig", "/var/www/symfony-project/templates/admin_order/index.html.twig");
